INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, is_active, created_at, updated_at, city_id, display_order) VALUES (13, '🏠 New Housing Units Completed', 'property_new_housing_units_completed_13', 'economy', 'housing', 'j67f-aayr', 'Count of net new housing units from completed building permits.', 'Tracks net new housing units that have been certified for occupancy through building permits. Each record represents when new units were approved for occupancy via Initial TCO, Amended TCO, or Certificate of Final Completion (CFC).', 'https://data.sfgov.org/Housing-and-Buildings/Dwelling-Unit-Completion-Counts-by-Building-Permit/j67f-aayr', 'SELECT date_trunc_ym(date_issued) as date, SUM(number_of_units_certified) as value WHERE date_issued >= last_year_start AND date_issued <= current_date GROUP BY date ORDER BY date', 'SELECT ''🏗️🏠 New Housing Units'' as label, max(date_issued) as max_date, SUM(CASE WHEN date_trunc_ym(date_issued) >= this_year_start AND date_trunc_ym(date_issued) <= this_year_end THEN number_of_units_certified END) as this_year, SUM(CASE WHEN date_trunc_ym(date_issued) >= last_year_start AND date_trunc_ym(date_issued) <= last_year_end THEN number_of_units_certified END) as last_year, (SUM(CASE WHEN date_trunc_ym(date_issued) >= this_year_start AND date_trunc_ym(date_issued) <= this_year_end THEN number_of_units_certified END) - SUM(CASE WHEN date_trunc_ym(date_issued) >= last_year_start AND date_trunc_ym(date_issued) <= last_year_end THEN number_of_units_certified END)) as delta, ((SUM(CASE WHEN date_trunc_ym(date_issued) >= this_year_start AND date_trunc_ym(date_issued) <= this_year_end THEN number_of_units_certified END) - SUM(CASE WHEN date_trunc_ym(date_issued) >= last_year_start AND date_trunc_ym(date_issued) <= last_year_end THEN number_of_units_certified END)) * 100.0 / NULLIF(SUM(CASE WHEN date_trunc_ym(date_issued) >= last_year_start AND date_trunc_ym(date_issued) <= last_year_end THEN number_of_units_certified END), 0)) as perc_diff', 'Dwelling Unit Completion Counts by Building Permit', 'Housing and Buildings', true, 'Units', 'up', '[]', '[]', '{"id": 13, "original_category": "economy", "original_subcategory": "housing", "original_subsubcategory": null}', true, '2025-05-27 20:34:15.325786', '2025-06-01 17:39:02.161974', 2, 13);
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, is_active, created_at, updated_at, city_id, display_order) VALUES (14, '🏢 New Business Registrations', 'business_new_business_registrations_14', 'economy', 'business', 'g8m3-pdis', 'Count of new business registrations in San Francisco.', 'Tracks new business registrations through the Treasurer & Tax Collector''s Office. Each registered business may have multiple locations. Data is collected through business registration applications and account updates.', 'https://data.sfgov.org/resource/g8m3-pdis.json', 'SELECT date_trunc_ymd(dba_start_date) as date, COUNT(*) as value WHERE dba_start_date >= last_year_start AND dba_start_date <= current_date GROUP BY date ORDER BY date', 'SELECT ''New Business Registrations'' as label, max(dba_start_date) as max_date, COUNT(CASE WHEN dba_start_date >= this_year_start AND dba_start_date <= this_year_end THEN 1 END) as this_year, COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end THEN 1 END) as last_year, (COUNT(CASE WHEN dba_start_date >= this_year_start AND dba_start_date <= this_year_end THEN 1 END) - COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end THEN 1 END)) as delta, ((COUNT(CASE WHEN dba_start_date >= this_year_start AND dba_start_date <= this_year_end THEN 1 END) - COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end THEN 1 END), 0)) as perc_diff, supervisor_district group by supervisor_district', '', '', true, 'Registrations', 'up', '[]', '[{"name": "naic_code_description", "fieldName": "naic_code_description", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}, {"name": "business_corridor", "fieldName": "business_corridor", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 14, "original_category": "economy", "original_subcategory": "business", "original_subsubcategory": null}', true, '2025-05-27 20:34:15.325786', '2025-06-01 17:39:02.161974', 2, 14);
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, is_active, created_at, updated_at, city_id, display_order) VALUES (15, '🚒 Fire Incidents YTD', 'fire_fire_incidents_ytd_8', 'safety', 'fire', 'wr8u-xric', 'Count of all non-medical incidents to which the SF Fire Department responded.', 'Fire Incidents includes a summary of each (non-medical) incident to which the SF Fire Department responded. Each incident record includes the call number, incident number, address, number and type of each unit responding, call type (as determined by dispatch), prime situation (field observation), actions taken, and property loss.', 'https://data.sfgov.org/resource/wr8u-xric.json', 'SELECT date_trunc_ymd(incident_date) as date, COUNT(*) as value WHERE incident_date >= last_year_start AND incident_date <= current_date GROUP BY date ORDER BY date', 'SELECT ''Total Fire Incidents'' as label, max(incident_date) as max_date, COUNT(CASE WHEN incident_date >= this_year_start AND incident_date <= this_year_end THEN 1 END) as this_year, COUNT(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN 1 END) as last_year, (COUNT(CASE WHEN incident_date >= this_year_start AND incident_date <= this_year_end THEN 1 END) - COUNT(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN 1 END)) as delta, ((COUNT(CASE WHEN incident_date >= this_year_start AND incident_date <= this_year_end THEN 1 END) - COUNT(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN 1 END), 0)) as perc_diff, supervisor_district group by supervisor_district', '', '', true, 'Incidents', 'down', '[]', '[{"name": "area_of_fire_origin", "fieldName": "area_of_fire_origin", "description": "Selected column for enhanced dashboard queries"}, {"name": "ignition_cause", "fieldName": "ignition_cause", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 8, "original_category": "safety", "original_subcategory": "fire", "original_subsubcategory": null}', true, '2025-05-27 20:34:15.325786', '2025-06-01 17:39:02.161974', 2, 15);
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, is_active, created_at, updated_at, city_id, display_order) VALUES (16, '💔 Fire Fatalities YTD', 'fatalities_fire_fatalities_ytd_9', 'safety', 'fire', 'wr8u-xric', 'Total count of fire-related fatalities, including both civilian and fire personnel fatalities.', 'Combines both fire fatalities (fire personnel) and civilian fatalities that occurred during fire incidents. Data is collected and reported by the SF Fire Department for each incident.', 'https://data.sfgov.org/resource/wr8u-xric.json', 'SELECT date_trunc_ymd(incident_date) as date, sum(fire_fatalities + civilian_fatalities) as value WHERE incident_date >= last_year_start AND incident_date <= current_date GROUP BY date ORDER BY date', 'SELECT ''Total Fatalities'' as label, max(incident_date) as max_date, SUM(CASE WHEN incident_date >= this_year_start AND incident_date <= this_year_end THEN fire_fatalities + civilian_fatalities END) as this_year, SUM(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN fire_fatalities + civilian_fatalities END) as last_year, (SUM(CASE WHEN incident_date >= this_year_start AND incident_date <= this_year_end THEN fire_fatalities + civilian_fatalities END) - SUM(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN fire_fatalities + civilian_fatalities END)) as delta, ((SUM(CASE WHEN incident_date >= this_year_start AND incident_date <= this_year_end THEN fire_fatalities + civilian_fatalities END) - SUM(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN fire_fatalities + civilian_fatalities END)) * 100.0 / NULLIF(SUM(CASE WHEN incident_date >= last_year_start AND incident_date <= last_year_end THEN fire_fatalities + civilian_fatalities END), 0)) as perc_diff, supervisor_district group by supervisor_district', '', '', true, 'Fatalities', 'down', '[]', '[{"name": "area_of_fire_origin", "fieldName": "area_of_fire_origin", "description": "Selected column for enhanced dashboard queries"}, {"name": "ignition_cause", "fieldName": "ignition_cause", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 9, "original_category": "safety", "original_subcategory": "fire", "original_subsubcategory": null}', true, '2025-05-27 20:34:15.325786', '2025-06-01 17:39:02.161974', 2, 16);
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, is_active, created_at, updated_at, city_id, display_order) VALUES (17, '🚫 Business Closures', 'closures_business_closures_15', 'economy', 'business', 'g8m3-pdis', 'Count of business closures in San Francisco.', 'Tracks business closures through the Treasurer & Tax Collector''s Office. Includes businesses that have formally closed or been administratively closed after 3 years of no filing or communication.', 'https://data.sfgov.org/resource/g8m3-pdis.json', 'SELECT date_trunc_ymd(dba_end_date) as date, COUNT(*) as value WHERE dba_end_date >= last_year_start AND dba_end_date <= current_date AND administratively_closed is null GROUP BY date ORDER BY date', 'SELECT ''Business Closures'' as label, max(dba_end_date) as max_date, COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND administratively_closed is null THEN 1 END) as this_year, COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END) as last_year, (COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND administratively_closed is null THEN 1 END) - COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END)) as delta, ((COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND administratively_closed is null THEN 1 END) - COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END), 0)) as perc_diff, supervisor_district group by supervisor_district', '', '', true, 'Closures', 'down', '[]', '[{"name": "naic_code_description", "fieldName": "naic_code_description", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}, {"name": "business_corridor", "fieldName": "business_corridor", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 15, "original_category": "economy", "original_subcategory": "business", "original_subsubcategory": null}', true, '2025-05-27 20:34:15.325786', '2025-06-01 17:39:02.161974', 2, 17);
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, is_active, created_at, updated_at, city_id, display_order) VALUES (18, '🏪 Retail Closures', 'retail_closures_retail_closures_17', 'economy', 'business', 'g8m3-pdis', 'Count of retail business closures in San Francisco.', 'Tracks retail business closures through the Treasurer & Tax Collector''s Office. Uses NAICS code to identify retail businesses. Includes businesses that have formally closed or been administratively closed after 3 years of no filing or communication.', 'https://data.sfgov.org/resource/g8m3-pdis.json', 'SELECT date_trunc_ymd(dba_end_date) as date, COUNT(*) as value WHERE dba_end_date >= last_year_start AND dba_end_date <= current_date AND naic_code_description = ''Retail Trade'' AND administratively_closed is null GROUP BY date ORDER BY date', 'SELECT ''Retail Closures'' as label, max(dba_end_date) as max_date, COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND naic_code_description = ''Retail Trade'' AND administratively_closed is null THEN 1 END) as this_year, COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND naic_code_description = ''Retail Trade'' AND administratively_closed is null THEN 1 END) as last_year, (COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND naic_code_description = ''Retail Trade'' AND administratively_closed is null THEN 1 END) - COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND naic_code_description = ''Retail Trade'' AND administratively_closed is null THEN 1 END)) as delta, ((COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND naic_code_description = ''Retail Trade'' AND administratively_closed is null THEN 1 END) - COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND naic_code_description = ''Retail Trade'' AND administratively_closed is null THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND naic_code_description = ''Retail Trade'' AND administratively_closed is null THEN 1 END), 0)) as perc_diff, supervisor_district group by supervisor_district', '', '', true, 'Closures', 'down', '[]', '[{"name": "naic_code_description", "fieldName": "naic_code_description", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}, {"name": "business_corridor", "fieldName": "business_corridor", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 17, "original_category": "economy", "original_subcategory": "business", "original_subsubcategory": null}', true, '2025-05-27 20:34:15.325786', '2025-06-01 17:39:02.161974', 2, 18);
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, is_active, created_at, updated_at, city_id, display_order) VALUES (22, '🚑 911 Response (minutes) - No danger to life or property', 'response_911_response_minutes_no_danger_to_life_or_property_12', 'safety', 'emergency_response', '2zdj-bwza', 'Average response time in minutes for Priority C emergency calls.', 'Measures the time between when a 911 call is received and when the first unit arrives on scene for Priority C (lowest priority, no danger to life or property) calls. Data comes from the Department of Emergency Management''s Computer Aided Dispatch (CAD) System. Only includes calls where both received and onscene times are recorded and valid.', 'https://data.sfgov.org/resource/2zdj-bwza.json', 'SELECT date_trunc_ymd(received_datetime) as date, AVG((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) as value WHERE received_datetime >= last_year_start AND received_datetime < current_date AND received_datetime IS NOT NULL AND onscene_datetime IS NOT NULL AND received_datetime < onscene_datetime AND priority_final = ''C'' GROUP BY date ORDER BY date', 'SELECT ''Priority C Response Time'' as label, max(received_datetime) as max_date, AVG(CASE WHEN received_datetime >= this_year_start AND received_datetime <= this_year_end THEN ((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) END) as this_year, AVG(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end THEN ((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) END) as last_year, (AVG(CASE WHEN received_datetime >= this_year_start AND received_datetime <= this_year_end THEN ((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) END) - AVG(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end THEN ((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) END)) as delta, ((AVG(CASE WHEN received_datetime >= this_year_start AND received_datetime <= this_year_end THEN ((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) END) - AVG(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end THEN ((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) END)) * 100.0 / NULLIF(AVG(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end THEN ((date_extract_y(onscene_datetime) - date_extract_y(received_datetime)) * 525600 + (date_extract_m(onscene_datetime) - date_extract_m(received_datetime)) * 43800 + (date_extract_d(onscene_datetime) - date_extract_d(received_datetime)) * 1440 + (date_extract_hh(onscene_datetime) * 60 + date_extract_mm(onscene_datetime)) - (date_extract_hh(received_datetime) * 60 + date_extract_mm(received_datetime))) END), 0)) as perc_diff, supervisor_district WHERE received_datetime IS NOT NULL AND onscene_datetime IS NOT NULL AND received_datetime < onscene_datetime AND priority_final = ''C'' GROUP BY supervisor_district', '', '', true, 'Responses', 'down', '[]', '[{"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 12, "original_category": "safety", "original_subcategory": "emergency_response", "original_subsubcategory": null}', true, '2025-05-27 20:34:15.325786', '2025-06-01 17:39:02.161974', 2, 22);
