INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (23, 'ğŸš¦ Traffic Related 911 Calls', 'traffic_related_911_calls', 'Traffic', '', '2zdj-bwza', 'Count of all 911 calls related to traffic incidents, with an added touch of efficiency.', 'This metric tracks the number of 911 calls related to traffic incidents, specifically including violations, citations, and stops. ', 'https://data.sfgov.org/Public-Safety/911-Emergency-Response-Calls-Data/2zdj-bwza', 'SELECT date_trunc_ymd(received_datetime) as date, COUNT(*) as value WHERE received_datetime >= last_year_start AND received_datetime <= current_date AND call_type_final IN (''585'', ''587'', ''585'') GROUP BY date ORDER BY date', 'SELECT ''Traffic Related 911 Calls'' as label, max(received_datetime) as max_date, COUNT(CASE WHEN received_datetime >= this_year_start AND received_datetime <= this_year_end AND call_type_final IN (''585'', ''587'') THEN 1 END) as this_year, COUNT(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end AND call_type_final IN (''585'', ''587'') THEN 1 END) as last_year, (COUNT(CASE WHEN received_datetime >= this_year_start AND received_datetime <= this_year_end AND call_type_final IN (''585'', ''587'') THEN 1 END) - COUNT(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end AND call_type_final IN (''585'', ''587'') THEN 1 END)) as delta, ((COUNT(CASE WHEN received_datetime >= this_year_start AND received_datetime <= this_year_end AND call_type_final IN (''585'', ''587'') THEN 1 END) - COUNT(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end AND call_type_final IN (''585'', ''587'') THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN received_datetime >= last_year_start AND received_datetime <= last_year_end AND call_type_final IN (''585'', ''587'') THEN 1 END), 0)) as perc_diff, supervisor_district GROUP BY supervisor_district', '911 Emergency Response Calls Data', 'Traffic', true, 'Traffic Calls', 'down', '"[{\"name\": \"supervisor_district\", \"fieldName\": \"supervisor_district\", \"description\": \"Supervisor district where the call was made\"}]"', '[{"name": "call_type_final_desc", "fieldName": "call_type_final_desc", "description": "Selected column for metric analysis"}, {"name": "agency", "fieldName": "agency", "description": "Selected column for metric analysis"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for metric analysis"}]', '{}', NULL, 9, true, '2025-06-01 17:54:01.932097', '2025-08-08 16:02:18.479566', '2025-08-07');
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (35, 'ğŸ“œ Eviction Notices', 'eviction_notices', 'Housing and Homelessness', 'housing', '5cei-gny5', 'Tracks the monthly count of eviction notices filed in San Francisco.', 'Counts of eviction notices filed with the Rent Board. This does not necessarily mean an eviction took place, as not all notices result in eviction.', 'https://data.sfgov.org/Housing-and-Buildings/Eviction-Notices/5cei-gny5', 'select count(*) as value, date_trunc_ym(file_date) as date where file_date>= last_year_start and file_date<= current_date group by date order by date desc', 'SELECT ''Eviction Notices'' as label, COUNT(CASE WHEN file_date >= this_year_start AND file_date < this_year_end THEN 1 END) as this_year, COUNT(CASE WHEN file_date >= last_year_start AND file_date < last_year_end THEN 1 END) as last_year, COUNT(CASE WHEN file_date >= this_year_start AND file_date < this_year_end THEN 1 END) - COUNT(CASE WHEN file_date >= last_year_start AND file_date < last_year_end THEN 1 END) as delta, (COUNT(CASE WHEN file_date >= this_year_start AND file_date < this_year_end THEN 1 END) - COUNT(CASE WHEN file_date >= last_year_start AND file_date < last_year_end THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN file_date >= last_year_start AND file_date < last_year_end THEN 1 END), 0) as perc_diff GROUP BY label', 'Eviction Notices', 'Housing and Homelessness', true, 'Notices', 'down', '[]', '[{"name": "non_payment", "fieldName": "non_payment", "description": "Selected column for metric analysis"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for metric analysis"}]', '{}', NULL, 15, true, '2025-07