INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (17, 'ğŸš« Business Closures', 'closures_business_closures_15', 'economy', 'business', 'g8m3-pdis', 'Count of business closures in San Francisco.', 'Tracks business closures through the Treasurer & Tax Collector''s Office. Includes businesses that have formally closed or been administratively closed after 3 years of no filing or communication.', 'https://data.sfgov.org/resource/g8m3-pdis.json', 'SELECT date_trunc_ymd(dba_end_date) as date, COUNT(*) as value WHERE dba_end_date >= last_year_start AND dba_end_date <= current_date AND administratively_closed is null GROUP BY date ORDER BY date', 'SELECT ''Business Closures'' as label, max(dba_end_date) as max_date, COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND administratively_closed is null THEN 1 END) as this_year, COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END) as last_year, (COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND administratively_closed is null THEN 1 END) - COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END)) as delta, ((COUNT(CASE WHEN dba_end_date >= this_year_start AND dba_end_date <= this_year_end AND administratively_closed is null THEN 1 END) - COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN dba_end_date >= last_year_start AND dba_end_date <= last_year_end AND administratively_closed is null THEN 1 END), 0)) as perc_diff, supervisor_district group by supervisor_district ', '', '', true, 'Closures', 'down', '[]', '[{"name": "naic_code_description", "fieldName": "naic_code_description", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}, {"name": "business_corridor", "fieldName": "business_corridor", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 15, "original_category": "economy", "original_subcategory": "business", "original_subsubcategory": null}', 2, 17, true, '2025-05-27 20:34:15.325786', '2025-08-08 16:04:29.896961', '2025-08-07');
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (4, 'ğŸ’Š Drug Crime Incidents', 'drug_drug_crime_incidents_4', 'crime', 'Crime', 'wg3w-h783', 'Count of reported drug crime incidents, including drug offenses and drug violations.', 'Drug crimes are defined as incidents categorized as: Drug Offense and Drug Violation.', 'https://data.sfgov.org/Public-Safety/Police-Department-Incident-Reports-2018-to-Present/wg3w-h783', 'SELECT date_trunc_ymd(Report_Datetime) as date, COUNT(*) as value WHERE Report_Datetime >= last_year_start AND Report_Datetime <= current_date AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') GROUP BY date ORDER BY date', 'SELECT ''Drug Crime'' as label, max(Report_Datetime) as max_date, COUNT(CASE WHEN Report_Datetime >= this_year_start AND Report_Datetime <= this_year_end AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') THEN 1 END) as this_year, COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') THEN 1 END) as last_year, (COUNT(CASE WHEN Report_Datetime >= this_year_start AND Report_Datetime <= this_year_end AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') THEN 1 END) - COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') THEN 1 END)) as delta, ((COUNT(CASE WHEN Report_Datetime >= this_year_start AND Report_Datetime <= this_year_end AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') THEN 1 END) - COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category IN (''Drug Offense'', ''Drug Violation'') THEN 1 END), 0)) as perc_diff, supervisor_district group by supervisor_district', '', '', true, 'Incidents', 'down', '[]', '[{"name": "report_type_description", "fieldName": "report_type_description", "description": "Selected column for enhanced dashboard queries"}, {"name": "incident_category", "fieldName": "incident_category", "description": "Selected column for enhanced dashboard queries"}, {"name": "incident_subcategory", "fieldName": "incident_subcategory", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 4, "original_category": "crime", "original_subcategory": "Crime", "original_subsubcategory": null}', 2, 4, true, '2025-05-27 20:34:15.325786', '2025-08-08 15:55:12.174645', '2025-08-07');
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (5, 'ğŸ”’ Jail Bookings', 'jail_jail_bookings_5', 'crime', 'jail', 'pfbp-75pz', 'Total count of individuals booked into San Francisco City and County Jail.', 'Tracks the total number of jail bookings. Each booking represents when an individual is processed into jail custody after arrest. The booking process includes recording personal information, fingerprinting, and photographing. A single person may have multiple bookings.', 'https://data.sfgov.org/Public-Safety/Sheriff-Jail-Bookings-by-Age-Group/pfbp-75pz', 'SELECT date_trunc_ym(month_and_year_of_booking) as date, SUM(count_of_bookings) as value WHERE month_and_year_of_booking >= last_year_start AND month_and_year_of_booking <= current_date GROUP BY date ORDER BY date', 'SELECT ''Total Bookings'' as label, max(month_and_year_of_booking) as max_date, SUM(CASE WHEN month_and_year_of_booking >= this_year_start AND month_and_year_of_booking <= this_year_end THEN count_of_bookings END) as this_year, SUM(CASE WHEN month_and_year_of_booking >= last_year_start AND month_and_year_of_booking <= last_year_end THEN count_of_bookings END) as last_year, (SUM(CASE WHEN month_and_year_of_booking >= this_year_start AND month_and_year_of_booking <= this_year_end THEN count_of_bookings END) - SUM(CASE WHEN month_and_year_of_booking >= last_year_start AND month_and_year_of_booking <= last_year_end THEN count_of_bookings END)) as delta, ((SUM(CASE WHEN month_and_year_of_booking >= this_year_start AND month_and_year_of_booking <= this_year_end THEN count_of_bookings END) - SUM(CASE WHEN month_and_year_of_booking >= last_year_start AND month_and_year_of_booking <= last_year_end THEN count_of_bookings END)) * 100.0 / NULLIF(SUM(CASE WHEN month_and_year_of_booking >= last_year_start AND month_and_year_of_booking <= last_year_end THEN count_of_bookings END), 0)) as perc_diff', 'Sheriff Jail Bokings', 'Crime', false, 'Bookings', 'down', '[]', '[{"name": "age_group", "fieldName": "age_group", "description": "Age range of the booked individual"}]', '{"id": 5, "original_category": "crime", "original_subcategory": "jail", "original_subsubcategory": null}', 2, 5, true, '2025-05-27 20:34:15.325786', '2025-08-08 15:55:30.759115', '2025-07-01');
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (6, 'ğŸš“ Arrests Presented to DA', 'arrests_arrests_presented_to_da_6', 'crime', 'DA', 'czsm-3ei3', 'Total count of arrests presented to the District Attorney''s office for review.', 'Count of all arrests presented to the District Attorney''s office for review. Each record represents an arrest that has been presented to the SFDA for a charging decision, regardless of the final action taken.', 'https://data.sfgov.org/Public-Safety/District-Attorney-Actions-Taken-on-Arrests-Present/czsm-3ei3', 'SELECT date_trunc_ymd(arrest_date) as date, COUNT(*) as value WHERE arrest_date >= last_year_start AND arrest_date <= current_date GROUP BY date ORDER BY date', 'SELECT ''Total Arrests Presented'' as label, max(arrest_date) as max_date, COUNT(CASE WHEN arrest_date >= this_year_start AND arrest_date <= this_year_end THEN 1 END) as this_year, COUNT(CASE WHEN arrest_date >= last_year_start AND arrest_date <= last_year_end THEN 1 END) as last_year, (COUNT(CASE WHEN arrest_date >= this_year_start AND arrest_date <= this_year_end THEN 1 END) - COUNT(CASE WHEN arrest_date >= last_year_start AND arrest_date <= last_year_end THEN 1 END)) as delta, ((COUNT(CASE WHEN arrest_date >= this_year_start AND arrest_date <= this_year_end THEN 1 END) - COUNT(CASE WHEN arrest_date >= last_year_start AND arrest_date <= last_year_end THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN arrest_date >= last_year_start AND arrest_date <= last_year_end THEN 1 END), 0)) as perc_diff', 'Sherriff Arrest ', 'crime', true, 'Arrests', 'up', '[]', '[{"name": "arresting_agency", "fieldName": "arresting_agency", "description": "Selected column for enhanced dashboard queries"}, {"name": "crime_type", "fieldName": "crime_type", "description": "Selected column for enhanced dashboard queries"}, {"name": "booked_case_type", "fieldName": "booked_case_type", "description": "Selected column for enhanced dashboard queries"}, {"name": "da_action_taken", "fieldName": "da_action_taken", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 6, "original_category": "crime", "original_subcategory": "DA", "original_subsubcategory": null}', 2, 6, true, '2025-05-27 20:34:15.325786', '2025-08-08 15:55:35.141047', '2025-08-02');
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (29, 'ğŸ’€ Homicides', 'homicides', 'crime', '', 'wg3w-h783', 'Count of reported homicide incidents.', 'Count of reported incidents specifically categorized as Homicide from the police department incident reports.', 'https://data.sfgov.org/Public-Safety/Police-Department-Incident-Reports-2018-to-Present/wg3w-h783', 'SELECT date_trunc_ymd(Report_Datetime) as date, COUNT(*) as value WHERE Report_Datetime >= last_year_start AND Report_Datetime <= current_date AND Incident_Category = ''Homicide'' GROUP BY date ORDER BY date', 'SELECT ''Homicide'' as label, max(Report_Datetime) as max_date, COUNT(CASE WHEN Report_Datetime >= this_year_start AND Report_Datetime <= this_year_end AND Incident_Category = ''Homicide'' THEN 1 END) as this_year, COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category = ''Homicide'' THEN 1 END) as last_year, (COUNT(CASE WHEN Report_Datetime >= this_year_start AND Report_Datetime <= this_year_end AND Incident_Category = ''Homicide'' THEN 1 END) - COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category = ''Homicide'' THEN 1 END)) as delta, ((COUNT(CASE WHEN Report_Datetime >= this_year_start AND Report_Datetime <= this_year_end AND Incident_Category = ''Homicide'' THEN 1 END) - COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category = ''Homicide'' THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN Report_Datetime >= last_year_start AND Report_Datetime <= last_year_end AND Incident_Category = ''Homicide'' THEN 1 END), 0)) as perc_diff, supervisor_district group by supervisor_district', 'Police Department Incident Reports', 'Public Safety', false, 'Incidents', 'down', '"[{\"name\": \"supervisor_district\", \"fieldName\": \"supervisor_district\", \"description\": \"Supervisor district where the incident occurred\"}]"', '[{"name": "incident_description", "fieldName": "incident_description", "description": "Selected column for metric analysis"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for metric analysis"}]', '{}', NULL, 1000, true, '2025-06-24 15:11:46.464277', '2025-08-08 15:55:59.24356', '2025-08-07');
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (12, 'ğŸ›ï¸ New Retail Registrations', 'retail_new_retail_registrations_16', 'economy', 'business', 'g8m3-pdis', 'Count of new retail business registrations in San Francisco.', 'Tracks new retail business registrations through the Treasurer & Tax Collector''s Office. Uses NAICS code to identify retail businesses. Each registered business may have multiple locations.', 'https://data.sfgov.org/resource/g8m3-pdis.json', 'SELECT date_trunc_ymd(dba_start_date) as date, COUNT(*) as value WHERE dba_start_date >= last_year_start AND dba_start_date <= current_date AND naic_code_description = ''Retail Trade'' GROUP BY date ORDER BY date', 'SELECT ''New Retail Registrations'' as label, max(dba_start_date) as max_date, COUNT(CASE WHEN dba_start_date >= this_year_start AND dba_start_date <= this_year_end AND naic_code_description = ''Retail Trade'' THEN 1 END) as this_year, COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end AND naic_code_description = ''Retail Trade'' THEN 1 END) as last_year, (COUNT(CASE WHEN dba_start_date >= this_year_start AND dba_start_date <= this_year_end AND naic_code_description = ''Retail Trade'' THEN 1 END) - COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end AND naic_code_description = ''Retail Trade'' THEN 1 END)) as delta, ((COUNT(CASE WHEN dba_start_date >= this_year_start AND dba_start_date <= this_year_end AND naic_code_description = ''Retail Trade'' THEN 1 END) - COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end AND naic_code_description = ''Retail Trade'' THEN 1 END)) * 100.0 / NULLIF(COUNT(CASE WHEN dba_start_date >= last_year_start AND dba_start_date <= last_year_end AND naic_code_description = ''Retail Trade'' THEN 1 END), 0)) as perc_diff, supervisor_district GROUP BY supervisor_district ', '', '', true, 'Registrations', 'up', '[]', '[{"name": "naic_code_description", "fieldName": "naic_code_description", "description": "Selected column for enhanced dashboard queries"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Selected column for enhanced dashboard queries"}, {"name": "business_corridor", "fieldName": "business_corridor", "description": "Selected column for enhanced dashboard queries"}]', '{"id": 16, "original_category": "economy", "original_subcategory": "business", "original_subsubcategory": null}', 2, 18, true, '2025-05-27 20:34:15.325786', '2025-08-08 16:04:52.598139', '2025-08-07');
INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date) VALUES (33, 'â³ Arrest to Conviction (days)', 'average_age_cases_da', 'crime', '', 'ynfy-z5kt', 'Average age (in days) of cases from arrest to DA action taken, calculated by breaking down date components and aggregating their differences.', 'Calculates the average duration (in days) from arrest to a DA action being recorded using date component extraction.', 'https://data.sfgov.org/Public-Safety/District-Attorney-Actions-Taken-on-Arrests-Presented/czsm-3ei3', 'SELECT date_trunc_ymd(disposition_date) as date, AVG((date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date))) as value WHERE disposition_date >= last_year_start AND disposition_date < current_date AND arrest_date IS NOT NULL AND disposition_date IS NOT NULL AND arrest_date < disposition_date AND case_status LIKE ''%Conviction%'' GROUP BY date_trunc_ymd(disposition_date)', 'SELECT ''Arrest to Conviction Time'' as label, MAX(disposition_date) as max_date, AVG(CASE WHEN disposition_date >= this_year_start AND disposition_date <= this_year_end THEN (date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date)) END) as this_year, AVG(CASE WHEN disposition_date >= last_year_start AND disposition_date <= last_year_end THEN (date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date)) END) as last_year, (AVG(CASE WHEN disposition_date >= this_year_start AND disposition_date <= this_year_end THEN (date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date)) END) - AVG(CASE WHEN disposition_date >= last_year_start AND disposition_date <= last_year_end THEN (date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date)) END)) as delta, ((AVG(CASE WHEN disposition_date >= this_year_start AND disposition_date <= this_year_end THEN (date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date)) END) - AVG(CASE WHEN disposition_date >= last_year_start AND disposition_date <= last_year_end THEN (date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date)) END)) * 100.0 / NULLIF(AVG(CASE WHEN disposition_date >= last_year_start AND disposition_date <= last_year_end THEN (date_extract_y(disposition_date) - date_extract_y(arrest_date)) * 365 + (date_extract_m(disposition_date) - date_extract_m(arrest_date)) * 30 + (date_extract_d(disposition_date) - date_extract_d(arrest_date)) END), 0)) as perc_diff WHERE disposition_date IS NOT NULL AND arrest_date IS NOT NULL AND arrest_date < disposition_date AND case_status LIKE ''%Conviction%''', 'District Attorney Actions Taken on Arrests Presented', 'Public Safety', false, 'Days', 'down', '[]', '[]', '{}', NULL, 1000, true, '2025-07-14 22:05:09.393421', '2025-08-08 15:55:42.354587', '2025-