INSERT INTO public.metrics (id, metric_name, metric_key, category, subcategory, endpoint, summary, definition, data_sf_url, ytd_query, metric_query, dataset_title, dataset_category, show_on_dash, item_noun, greendirection, location_fields, category_fields, metadata, city_id, display_order, is_active, created_at, updated_at, most_recent_data_date, map_query, map_filters, map_config) VALUES (14, '🏢 New Business Registrations', 'business_new_business_registrations_14', 'economy', 'business', 'g8m3-pdis', 'Count of new business registrations and location expansions in San Francisco. Includes both new businesses entering the market and existing businesses opening new locations.', 'Count of new business registrations (dba_start_date) and location expansions (location_start_date for existing businesses). This comprehensive approach captures all business opening activity in San Francisco.', 'https://data.sfgov.org/resource/g8m3-pdis.json', 'SELECT date_trunc_ymd(CASE WHEN dba_start_date >= last_year_start THEN dba_start_date ELSE location_start_date END) as date, COUNT(*) as value
WHERE ((dba_start_date >= last_year_start AND dba_start_date <= current_date) OR (location_start_date >= last_year_start AND location_start_date <= current_date AND dba_start_date < last_year_start))
GROUP BY date ORDER BY date', 'SELECT ''New Business Registrations'' as label,
       MAX(GREATEST(dba_start_date, location_start_date)) as max_date,
       COUNT(CASE WHEN (dba_start_date >= this_year_start AND dba_start_date <= this_year_end) OR (location_start_date >= this_year_start AND location_start_date <= this_year_end AND dba_start_date < this_year_start) THEN 1 END) as this_year,
       COUNT(CASE WHEN (dba_start_date >= last_year_start AND dba_start_date <= last_year_end) OR (location_start_date >= last_year_start AND location_start_date <= last_year_end AND dba_start_date < last_year_start) THEN 1 END) as last_year,
       (COUNT(CASE WHEN (dba_start_date >= this_year_start AND dba_start_date <= this_year_end) OR (location_start_date >= this_year_start AND location_start_date <= this_year_end AND dba_start_date < this_year_start) THEN 1 END) - 
        COUNT(CASE WHEN (dba_start_date >= last_year_start AND dba_start_date <= last_year_end) OR (location_start_date >= last_year_start AND location_start_date <= last_year_end AND dba_start_date < last_year_start) THEN 1 END)) as delta,
       ((COUNT(CASE WHEN (dba_start_date >= this_year_start AND dba_start_date <= this_year_end) OR (location_start_date >= this_year_start AND location_start_date <= this_year_end AND dba_start_date < this_year_start) THEN 1 END) - 
