         NULLIF(COUNT(CASE WHEN (dba_start_date >= last_year_start AND dba_start_date <= last_year_end) OR (location_start_date >= last_year_start AND location_start_date <= last_year_end AND dba_start_date < last_year_start) THEN 1 END), 0)) as perc_diff, supervisor_district GROUP BY supervisor_district', '', '', true, 'Registrations', 'up', '[]', '[{"name": "naic_code_description", "fieldName": "naic_code_description", "description": "Business category/industry"}, {"name": "opening_type", "fieldName": "CASE WHEN dba_start_date >= this_year_start THEN ''New Business Registration'' ELSE ''New Location Opening'' END", "description": "Type of opening - new business vs new location"}, {"name": "supervisor_district", "fieldName": "supervisor_district", "description": "Supervisor district"}, {"name": "business_corridor", "fieldName": "business_corridor", "description": "Business corridor"}]', '{"id": 14, "original_category": "economy", "original_subcategory": "business", "original_subsubcategory": null}', 2, 16, true, '2025-05-27 20:34:15.325786', '2025-08-18 10:22:09.58732', '2025-08-16', 'SELECT dba_name, naic_code_description, business_corridor, neighborhoods_analysis_boundaries, supervisor_district, administratively_closed, location, dba_start_date, location_start_date', '{"date_range": {"field": "CASE WHEN dba_start_date >= last_year_start THEN dba_start_date ELSE location_start_date END", "fallback_field": "location_start_date", "fallback_condition": "dba_start_date < last_year_start"}}', '{"date_field": "CASE WHEN dba_start_date >= last_year_start THEN dba_start_date ELSE location_start_date END", "location_field": "location", "title_template": "{metric_name}", "supports_districts": true, "data_point_threshold": 2000, "chart_type_preference": "symbol", "supports_time_periods": true}');
