       COUNT(CASE WHEN ((dba_end_date >= last_year_start AND dba_end_date <= last_year_end) OR (location_end_date >= last_year_start AND location_end_date <= last_year_end AND (dba_end_date IS NULL OR dba_end_date > last_year_end))) AND naic_code_description = ''Retail Trade'' THEN 1 END) as last_year,
       (COUNT(CASE WHEN ((dba_end_date >= this_year_start AND dba_end_date <= this_year_end) OR (location_end_date >= this_year_start AND location_end_date <= this_year_end AND (dba_end_date IS NULL OR dba_end_date > this_year_end))) AND naic_code_description = ''Retail Trade'' THEN 1 END) - 
        COUNT(CASE WHEN ((dba_end_date >= last_year_start AND dba_end_date <= last_year_end) OR (location_end_date >= last_year_start AND location_end_date <= last_year_end AND (dba_end_date IS NULL OR dba_end_date > last_year_end))) AND naic_code_description = ''Retail Trade'' THEN 1 END)) as delta,
